import numpy as np 
import matplotlib.pyplot as plt
import networkx as nx
import random


def Dijkstra(graph, root, dest):
    try:
        return nx.dijkstra_path(graph, root, dest)
    except nx.NodeNotFound:
        return None
    except nx.NetworkXNoPath:
        return None

